import React from 'react';
import { useSudokuContext } from '../../context/SudokuContext';

// Sudoku Board
export const GameSection = (props) => {

  const rows = [0,1,2,3,4,5,6,7,8];
  let { numberSelected,
        gameArray,
        fastMode,
        cellSelected,
        initArray } = useSudokuContext();

  /**
   * 单元格突出显示方法1：突出显示所有单元格
   * 与当前单元格相关。 通过相关，我的意思是所有
   * 与当前单元格在同一行/列/框中的单元格。
   */
  // eslint-disable-next-line
  function _isCellRelatedToSelectedCell(row, column) {
    if (cellSelected === row * 9 + column) {
      return true;
    }
    let rowOfSelectedCell = Math.floor(cellSelected / 9);
    let columnOfSelectedCell = cellSelected % 9;
    if (rowOfSelectedCell === row || columnOfSelectedCell === column) {
      return true;
    }
    return [  [0,3,0,3],
              [0,3,3,6],
              [0,3,6,9],
              [3,6,0,3],
              [3,6,3,6],
              [3,6,6,9],
              [6,9,0,3],
              [6,9,3,6],
              [6,9,6,9]
            ].some((array) => {
              if (rowOfSelectedCell > array[0]-1 && row > array[0]-1 &&
                  rowOfSelectedCell < array[1] && row < array[1] &&
                  columnOfSelectedCell > array[2]-1 && column > array[2]-1 &&
                  columnOfSelectedCell < array[3] && column < array[3])
                  return true;
              return false;
            });
  }

  /**
   * 单元格突出显示方法 2：突出显示所有单元格
   * 与当前单元格中的数字相同。
   */
  function _isCellSameAsSelectedCell(row, column) {
    if (fastMode) {
      if (numberSelected === gameArray[row * 9 + column]) {
        return true;
      }
      return false;
    } else {
      if (cellSelected === row * 9 + column) {
        return true;
      }
      if (gameArray[cellSelected] === '0') {
        return false;
      }
      if (gameArray[cellSelected] === gameArray[row * 9 + column]) {
        return true;
      }
    }
  }


  function highlighted_cell(index, value, highlight) {
    if (value !== '0') {
      if (initArray[index] === '0') {
        return (
          <td className={`game__cell game__cell--userfilled game__cell--${highlight}selected`} key={index} onClick={() => props.onClick(index)}>{value}</td>
        )
      } else {
        return (
          <td className={`game__cell game__cell--filled game__cell--${highlight}selected`} key={index} onClick={() => props.onClick(index)}>{value}</td>
        )
      }
    } else {
      return (
        <td className={`game__cell game__cell--${highlight}selected`} key={index} onClick={() => props.onClick(index)}>{value}</td>
      )
    }
  }

  /**
   * 返回与所选单元格不相关的类或单元格。
   */
  function normal_cell(index, value) {
    if (value !== '0') {
      if (initArray[index] === '0') {
        return (
          <td className="game__cell game__cell--userfilled" key={index} onClick={() => props.onClick(index)}>{value}</td>
        )
      } else {
        return (
          <td className="game__cell game__cell--filled" key={index} onClick={() => props.onClick(index)}>{value}</td>
        )
      }
    } else {
      return (
        <td className="game__cell" key={index} onClick={() => props.onClick(index)}>{value}</td>
      )
    }
  }

  // Sudoku Board [9x9]
  return (
    <div className="sudoku_game">
      <table id="sudoku_board">
        <tbody>{
          rows.map((row) => {
            return (
              <tr className="sudoku_row" key={row}>{
                  rows.map((column) => {
                    const index = (row * 9) + column;
                    const value = gameArray[index];

                    if (cellSelected === index) return highlighted_cell(index, value, 'highlight');



                    if (fastMode) {
                      if (numberSelected !== '0' && _isCellSameAsSelectedCell(row, column)) {
                        return highlighted_cell(index, value, '');
                      } else {
                        return normal_cell(index, value);
                      }
                    } else {
                      if (cellSelected !== -1 && _isCellSameAsSelectedCell(row, column)) {
                        return highlighted_cell(index, value, '');
                      } else {
                        return normal_cell(index, value);
                      }
                    }
                  })
              }</tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}
