import React from 'react';
import { useSudokuContext } from '../context/SudokuContext';

// Difficult Drop Down [Easy, Medium, Hard]
export function Difficulty (props) {

  let { difficulty } = useSudokuContext();

  return (
    <div id="status_section">
      <span id="difficulty_header">Difficulty:&nbsp;&nbsp;</span>
      <select name="status__difficulty-select" className="status__difficulty-select" defaultValue={difficulty} onChange={props.onChange}>
        <option value="Easy">Easy</option>
        <option value="Medium">Medium</option>
        <option value="Hard">Hard</option>
      </select>
    </div>
  )
}
