        new_pokemon_row.appendChild(pkm_data_expand);
        pokedex.appendChild(new_pokemon_row);
        pokedex.appendChild(pkm_full_info);

      })
      .catch((error) => {
        console.log('There was an ERROR: ', error);
      });
    }






    











