import React, { useState } from 'react';
import Pokedex from 'pokedex-promise-v2';

import '../style/pokemonpage.css';

function PokemonPage() {
    console.log();

    const P = new Pokedex();



    const test = async() => {
      try {
        const golduckSpecies = await P.getPokemonSpeciesByName("golduck")
        const frenchName = golduckSpecies.names.filter(pokeAPIName => pokeAPIName.language.name === 'fr')[0].name
        console.log(frenchName)
      } catch (error) {
          throw error
      }
    }

    const test2 = () => {
      P.getPokemonByName(['eevee', 'ditto']) // with Promise
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log('There was an ERROR: ', error);
      });
      
    }


    const NUM_ALL_POKEMON = 898;

    const list_of_all_pokemon_id = [];
    for (let i = 1; i <= NUM_ALL_POKEMON; i++) {
      list_of_all_pokemon_id.push(i);
    }
  
    

    const test3 = () => {

      let pokedex = document.getElementById("pokedex_table");

      


       P.getPokemonByName(1) // with Promise
      .then((response) => {
        console.log(response);
        const current_pokemon = response;
        const curr_pkm_name = response.species.name;
        const curr_pkm_hp = response.stats[0].base_stat;
        const curr_pkm_atk = response.sprites.front_default;

        const pokemon_sprite = document.createElement("src");
        pokemon_sprite.src = curr_pkm_atk;

        let new_pokemon_row = document.createElement('tr');

        let pokemon_data_sprite = document.createElement('td');
        pokemon_data_sprite.innerHTML = curr_pkm_name;

        let pokemon_data_name = document.createElement('td');
        pokemon_data_name.innerHTML = curr_pkm_hp;

        let pokemon_data_type = document.createElement('td');
        pokemon_data_type.appendChild(pokemon_sprite);

        new_pokemon_row.appendChild(pokemon_data_sprite);
        new_pokemon_row.appendChild(pokemon_data_name);
        new_pokemon_row.appendChild(pokemon_data_type);
        pokedex.appendChild(new_pokemon_row);


      })
      .catch((error) => {
        console.log('There was an ERROR: ', error);
      });
    }


    return (
      <div className="pkm_pg">
	      New PAGE
        

        <div> {/* Just some test functions*/}
          <button onClick={test}>
            Test
          </button>
          <button onClick={test2}>
            Test 2
          </button>
          <button onClick={test3}>
            Test 3
          </button>
        </div>

        <div> {/* Display the pokedex */}
          <table id="pokedex_table">
            <thead>
              <tr>
                  <th>name</th>
                  <th>height</th>
                  <th>place</th>
              </tr>
              </thead>
              <tbody>
              <tr>
                  <td>Monte Falco</td>
                  <td>1658</td>
                  <td>Parco Foreste Casentinesi</td>
              </tr>
              <tr>
                  <td>Monte Falterona</td>
                  <td>1654</td>
                  <td>Parco Foreste Casentinesi</td>
              </tr>
              </tbody>
          </table>
        </div>

      </div>
    );
}

export default PokemonPage;
