import React, { useState } from 'react';
import Pokedex from 'pokedex-promise-v2';

import '../style/pokemonpage.css';

function PokemonPage() {
    console.log();

    const P = new Pokedex();



    const test = async() => {
      try {
        const golduckSpecies = await P.getPokemonSpeciesByName("golduck")
        const frenchName = golduckSpecies.names.filter(pokeAPIName => pokeAPIName.language.name === 'fr')[0].name
        console.log(frenchName)
      } catch (error) {
          throw error
      }
    }

    const test2 = () => {
      P.getPokemonByName(['eevee', 'ditto']) // with Promise
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log('There was an ERROR: ', error);
      });
      
    }


    const NUM_ALL_POKEMON = 898;

    const list_of_all_pokemon_id = [];
    for (let i = 1; i <= NUM_ALL_POKEMON; i++) {
      list_of_all_pokemon_id.push(i);
    }
  




    const test3 = () => {

      let pokedex = document.getElementById("pokedex_table");

       P.getPokemonByName(1) // with Promise
      .then((response) => {
        console.log(response);
        const current_pokemon = response;


        const image_p = response.sprites.other.dream_world.front_default; // Clean line of Pokemon
        const image_p1 = response.sprites.other.home.front_default; // 3D Image of Pokemon
        const image_p2 = response.sprites.other['official-artwork'].front_default; // Official Art of Pokemon
        const image_p3 = response.sprites.versions['generation-i']['red-blue'].front_default;
        const image_p4 = response.sprites.versions['generation-i']['yellow'].front_default;
        const image_p5 = response.sprites.versions['generation-ii']['crystal'].front_default;
        const image_p6 = response.sprites.versions['generation-ii']['gold'].front_default;
        const image_p7 = response.sprites.versions['generation-ii']['silver'].front_default;
        const image_p8 = response.sprites.versions['generation-iii']['emerald'].front_default;
        const image_p9 = response.sprites.versions['generation-iii']['firered-leafgreen'].front_default;
        const image_p10 = response.sprites.versions['generation-iii']['ruby-sapphire'].front_default;
        const image_p11 = response.sprites.versions['generation-iv']['diamond-pearl'].front_default;
        const image_p12 = response.sprites.versions['generation-iv']['heartgold-soulsilver'].front_default;
        const image_p13 = response.sprites.versions['generation-iv']['platinum'].front_default;
        const image_p14 = response.sprites.versions['generation-v']['black-white'].front_default;
        const image_p15 = response.sprites.versions['generation-vi']['omegaruby-alphasapphire'].front_default;
        const image_p16 = response.sprites.versions['generation-vi']['x-y'].front_default;
        const image_p17 = response.sprites.versions['generation-vii']['icons'].front_default;
        const image_p18 = response.sprites.versions['generation-vii']['ultra-sun-ultra-moon'].front_default;
        const image_p19 = response.sprites.versions['generation-viii']['icons'].front_default;





        let pkm_full_info = document.createElement('tr');
        pkm_full_info.className = "pkm_expanded_info";

        

        let pkm_full_info_data = document.createElement('td');
        pkm_full_info_data.rowSpan = "1";

        let pkm_i = document.createElement('img'); 
        pkm_i.src = image_p2;

        let temp = document.createElement('div');
        temp.appendChild(pkm_i);
        let temp2 = document.createElement('div');
        temp2.innerText = "hello";
        temp.appendChild(temp2);
        pkm_full_info_data.appendChild(temp);

        
          
        pkm_full_info.appendChild(pkm_full_info_data);
        

        

        //const curr_pkm_weight = current_pokemon.weight;
        //const curr_pkm_height = current_pokemon.height;





        // Current Pokemon ID
        const curr_pkm_id = current_pokemon.id;
        const curr_pkm_name = response.name;
        const curr_pkm_sprite = response.sprites.front_default;
        // Current Pokemon Stats
        const curr_pkm_hp     = current_pokemon.stats[0].base_stat;
        const curr_pkm_atk    = current_pokemon.stats[1].base_stat;
        const curr_pkm_def    = current_pokemon.stats[2].base_stat;
        const curr_pkm_sp_atk = current_pokemon.stats[3].base_stat;
        const curr_pkm_sp_def = current_pokemon.stats[4].base_stat;
        const curr_pkm_speed  = current_pokemon.stats[5].base_stat;
        const curr_pkm_total  = curr_pkm_hp + curr_pkm_atk + curr_pkm_def + curr_pkm_sp_atk + curr_pkm_sp_def + curr_pkm_speed;


        const curr_pkm_type_1 = current_pokemon.types[0];
        const curr_pkm_type_2 = current_pokemon.types[1];
        
        

        console.log(curr_pkm_type_1);
        console.log(curr_pkm_type_2);


        // Add a new row to the pokedex
        let new_pokemon_row = document.createElement('tr');
        
        // Current Pokemon Information (Id, name, image)
        let pkm_data_id = document.createElement('td');
        pkm_data_id.innerText = curr_pkm_id;
        let pkm_data_name = document.createElement('td');
        pkm_data_name.innerText = curr_pkm_name;
        let pkm_data_sprite = document.createElement('td');
        let pkm_data_img = document.createElement('img'); 
        pkm_data_img.src = curr_pkm_sprite;
        pkm_data_sprite.appendChild(pkm_data_img);
        // Check if pokemon is multi-typed
        let pkm_data_type = document.createElement('td');
        let pkm_data_type_1 = document.createElement('div');
        pkm_data_type_1.innerText = curr_pkm_type_1.type.name.toUpperCase();
        pkm_data_type.appendChild(pkm_data_type_1);
        if (curr_pkm_type_2 !== undefined) {
          let pkm_data_type_2 = document.createElement('div');
          pkm_data_type_2.innerText = curr_pkm_type_2.type.name.toUpperCase();
          pkm_data_type.appendChild(pkm_data_type_2);
        }

        



        // Giving the table columns the corresponding values
        let pkm_data_hp = document.createElement('td');
        pkm_data_hp.innerText = curr_pkm_hp;
        let pkm_data_atk = document.createElement('td');
        pkm_data_atk.innerText = curr_pkm_atk;
        let pkm_data_def = document.createElement('td');
        pkm_data_def.innerText = curr_pkm_def;
        let pkm_data_sak = document.createElement('td');
        pkm_data_sak.innerText = curr_pkm_sp_atk;
        let pkm_data_sdf = document.createElement('td');
        pkm_data_sdf.innerText = curr_pkm_sp_def;
        let pkm_data_spd = document.createElement('td');
        pkm_data_spd.innerText = curr_pkm_speed;
        let pkm_data_total = document.createElement('td');
        pkm_data_total.innerText = curr_pkm_total;



        let pkm_data_expand = document.createElement('td');

        let pkm_modal = document.createElement('div');
        pkm_modal.className = "full_pkm_info_modal";
        let pkm_modal_content = document.createElement('div');
        pkm_modal_content.className = "full_pkm_info_modal_content";
        
        let pkm_modal_span = document.createElement('span');
        pkm_modal_span.className = "full_pkm_info_modal_close";
        let pkm_modal_div1 = document.createElement('div');
        let pkm_modal_div2 = document.createElement('div');

        pkm_modal_span.innerText = 'close';
        pkm_modal_span.onClick = function() {
          console.log('assada')
          pkm_modal.style.display = "none"; 
        }
        let pkm_modal_close = document.createElement('span');
        pkm_modal_close.innerHTML = "Some text in the Modal..";

        pkm_modal_content.appendChild(pkm_modal_span);
        pkm_modal_content.appendChild(pkm_modal_close);
        pkm_modal.appendChild(pkm_modal_content);

        pkm_data_expand.appendChild(pkm_modal);

        // Expand the pokemon if the user wishes to chose to
        pkm_data_expand.onclick = function(){

          if (pkm_modal.style.display === "none" || pkm_modal.style.display === "") {
            pkm_modal.style.display = "block"; 
          } 
        };  




        




        // Add each column & their value to the row and then add the row to the table (pokedex)
        new_pokemon_row.appendChild(pkm_data_id);
        new_pokemon_row.appendChild(pkm_data_name);
        new_pokemon_row.appendChild(pkm_data_sprite);
        new_pokemon_row.appendChild(pkm_data_type);
        new_pokemon_row.appendChild(pkm_data_spd);
        new_pokemon_row.appendChild(pkm_data_hp);
        new_pokemon_row.appendChild(pkm_data_atk);
        new_pokemon_row.appendChild(pkm_data_def);
        new_pokemon_row.appendChild(pkm_data_sak);
        new_pokemon_row.appendChild(pkm_data_sdf);
        new_pokemon_row.appendChild(pkm_data_spd);
        new_pokemon_row.appendChild(pkm_data_total);
        new_pokemon_row.appendChild(pkm_data_expand);
        pokedex.appendChild(new_pokemon_row);
        pokedex.appendChild(pkm_full_info);

      })
      .catch((error) => {
        console.log('There was an ERROR: ', error);
      });
    }


    return (
      <div className="pkm_pg">
        <head>

        </head>
	  
        <body>
          New PAGE

          <div> {/* Just some test functions*/}
            <button onClick={test}>
              Test
            </button>
            <button onClick={test2}>
              Test 2
            </button>
            <button onClick={test3}>
              Test 3
            </button>
          </div>

          <div> {/* Display the pokedex */}
            <table id="pokedex_table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Image</th>
                  <th>Name</th>
                  <th>Type</th>
                  <th>HP</th>
                  <th>ATK</th>
                  <th>DEF</th>
                  <th>SP ATK</th>
                  <th>SP DEF</th>
