        pkm_data_expand.innerText = "o";
        

        // Expand the pokemon if the user wishes to chose to
        pkm_data_expand.onclick = function(){
          document.getElementById("pkm_modal_name").innerText = response.name.replace(/\b\w/g, (c) => c.toUpperCase());
