import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

import mario_ground from './images/mario_ground.png';
import mario_pipe from './images/mario_pipe.png';
import mario_sprite from './images/mario_character_sprites/mario_idle.png';

function App() {

  // moving will change the sprite appearance
  const [marioSprite, setMarioSprite] = useState(0);


  const start_game = () => {
    console.log('start resume');
    // pipe spawns
    document.getElementById("start_pipe").style.display = "block";
    // mario jumps and runs to the right
    document.getElementById("mario_sprite").style.display = "block";
    run_mario_animation();
  }

  const run_mario_animation = () => {
    console.log('run animation');
    const mario = document.getElementById("mario_sprite").style.left = "100px";
    const X = window.width() - document.getElementById('mario_sprite').width();
    const Y = window.height() - document.getElementById('mario_sprite').height();
    const speed = 500;
    document.getElementById("mario_sprite").animate(
      {'top': X + 'px',
      'left': Y + 'px'}, speed, function() {
        run_mario_animation();
      });

  }


  const [playerX, setPlayerX] = useState(0);
  const [playerY, setPlayerY] = useState(0);



  return (
    <div className="App">

      <body className="parent">

        <div id="page_menu">
          DAVID LEYDON
        </div>
        <div id="page_content">
          <div id="interactive_text">
            <span>I</span>
            <span>n</span>
            <span>t</span>
            <span>e</span>
            <span>r</span>
            <span>a</span>
            <span>c</span>
            <span>t</span>
            <span>i</span>
            <span>v</span>
            <span>e</span>
            <span> </span>
            <span>R</span>
            <span>e</span>
            <span>s</span>
            <span>u</span>
            <span>m</span>
            <span>e</span>
          </div>
          <div id="interactive_small_text">
            The website is an interactive application, displaying both my resume & some skills I've learned along the way.
          </div>
          <button id="interactive_start" onClick={start_game}>
            PLAY
          </button>

        </div>
        <div>
          <img id="start_pipe" src={mario_pipe} alt="spawn pipe"/>
          <img id="mario_sprite" src={mario_sprite} alt="mario character sprite"></img>
          <img id="ground" src={mario_ground} alt="ground"/>

        </div>

      </body>
    </div>
  );
}

export default App;
